import { Component, OnInit } from '@angular/core';

import { ActivatedRoute } from '@angular/router';

import {Product, products} from '../products'

export interface category{
  name: string;
}

export const categories = [
  {
    name : 'Phones'
  },
  {
    name : 'PC'
  }
]
@Component({
  selector: 'app-category',
  templateUrl: './category.component.html',
  styleUrls: ['./category.component.css']
})


export class CategoryComponent {

  producrList : Product[] = []

  constructor(private route: ActivatedRoute){ }
  categories = [...categories]
  currentCategory :String =""
  ngOnInit() {
    // First get the product id from the current route.
    const routeParams = this.route.snapshot.paramMap;
    const categoryFromRoute = String(routeParams.get('category'));


  }
  getValue(buttonValue: string) {
    this.currentCategory = buttonValue

  }

  categoryy(){
    window.alert()
    for (let i =0; i<products.length; i++){
      if (products[i].category === this.currentCategory){
        this.producrList.push(products[i])
      }
    }
    return this.producrList;
  }
}
<div class = "category" *ngFor="let category of categories">
    <a [routerLink]="['/category', category.name]"   (click)="getValue(category.name)">{{category.name}} </a>
</div>
